
# include "../vector.hpp"

std::map<std::string, ptxt> VectorProgram::make_bits(RuntimeContext &info)
{
    std::map<std::string, ptxt> bits;
    add_bitstring(bits, "000000000001110000000000000", info);
    add_bitstring(bits, "000000100000000000000000000", info);
    add_bitstring(bits, "000000010000000000000000000", info);
    add_bitstring(bits, "000000000000001000001000000", info);
    add_bitstring(bits, "000000000000000000000000001", info);
    add_bitstring(bits, "010000000000000000000000000", info);
    add_bitstring(bits, "000000000000000000000000010", info);
    add_bitstring(bits, "000000000001000000010000000", info);
    add_bitstring(bits, "110000000000110001000000000", info);
    add_bitstring(bits, "000000000000000001000000000", info);
    add_bitstring(bits, "100000000000000000010000000", info);
    add_bitstring(bits, "000000000000000000000100000", info);
    add_bitstring(bits, "000000000000000000000100010", info);
    add_bitstring(bits, "000000001110000000100000000", info);
    return bits;
}

std::vector<ctxt> VectorProgram::initialize_temps(RuntimeContext &info)
{
    std::vector<ctxt> ts(7);
    ts[0] = encrypt_input("", info);
    ts[1] = encrypt_input("", info);
    ts[2] = encrypt_input("", info);
    ts[3] = encrypt_input("", info);
    return ts;
}

ctxt VectorProgram::computation(std::vector<ctxt> ts, std::map<std::string, ptxt> bits, RuntimeContext &info)
{
    seal::RelinKeys rk = info.keys->rk;
    seal::GaloisKeys gk = info.keys->gk;

    ctxt vs[6];
    ctxt ss[9];

    vs[0] = ts[0]; // vector load instr
    vs[1] = ts[2]; // vector load instr
    info.eval->multiply(vs[0], vs[1], vs[2]); // __v2 = __v0 * __v1
    info.eval->relinearize_inplace(vs[2], rk);
    info.eval->rotate_rows(vs[2], -26, gk, ss[0]); // __s0 = __v2 >> 26
    info.eval->rotate_rows(vs[2], -8, gk, ss[1]); // __s1 = __v2 >> 8
    info.eval->rotate_rows(vs[2], -2, gk, ss[2]); // __s2 = __v2 >> 2
    info.eval->rotate_rows(vs[2], -3, gk, ss[3]); // __s3 = __v2 >> 3
    info.eval->multiply(vs[0], vs[1], vs[3]); // __v3 = __v0 * __v1
    info.eval->relinearize_inplace(vs[3], rk);
    
    // __t4 = blend(__s3@100000000000000000010000000, __s0@010000000000000000000000000, __s1@000000000001110000000000000, __s2@000000000000000001000000000, __v2@000000000000000000000100010)
    ctxt t4_1, t4_2, t4_3, t4_4, t4_5;
    info.eval->multiply_plain(ss[3], bits["100000000000000000010000000"], t4_1);
    info.eval->multiply_plain(ss[0], bits["010000000000000000000000000"], t4_2);
    info.eval->multiply_plain(ss[1], bits["000000000001110000000000000"], t4_3);
    info.eval->multiply_plain(ss[2], bits["000000000000000001000000000"], t4_4);
    info.eval->multiply_plain(vs[2], bits["000000000000000000000100010"], t4_5);
    info.eval->add_many({t4_1, t4_2, t4_3, t4_4, t4_5}, ts[4]);
    
    
    // __t5 = blend(__v2@110000000000110001000000000, __v3@000000000001000000010000000, __s0@000000000000000000000100000, __s2@000000000000000000000000010)
    ctxt t5_1, t5_2, t5_3, t5_4;
    info.eval->multiply_plain(vs[2], bits["110000000000110001000000000"], t5_1);
    info.eval->multiply_plain(vs[3], bits["000000000001000000010000000"], t5_2);
    info.eval->multiply_plain(ss[0], bits["000000000000000000000100000"], t5_3);
    info.eval->multiply_plain(ss[2], bits["000000000000000000000000010"], t5_4);
    info.eval->add_many({t5_1, t5_2, t5_3, t5_4}, ts[5]);
    
    info.eval->add(ts[4], ts[5], vs[4]); // __v4 = __t4 + __t5
    info.eval->rotate_rows(vs[4], -26, gk, ss[4]); // __s4 = __v4 >> 26
    info.eval->rotate_rows(vs[4], -6, gk, ss[5]); // __s5 = __v4 >> 6
    info.eval->rotate_rows(vs[4], -24, gk, ss[6]); // __s6 = __v4 >> 24
    info.eval->rotate_rows(vs[4], -16, gk, ss[7]); // __s7 = __v4 >> 16
    info.eval->rotate_rows(vs[4], -22, gk, ss[8]); // __s8 = __v4 >> 22
    
    // __t6 = blend(__s7@000000100000000000000000000, __s5@000000010000000000000000000, __s6@000000001110000000100000000, __s8@000000000000001000001000000, __s4@000000000000000000000000001)
    ctxt t6_1, t6_2, t6_3, t6_4, t6_5;
    info.eval->multiply_plain(ss[7], bits["000000100000000000000000000"], t6_1);
    info.eval->multiply_plain(ss[5], bits["000000010000000000000000000"], t6_2);
    info.eval->multiply_plain(ss[6], bits["000000001110000000100000000"], t6_3);
    info.eval->multiply_plain(ss[8], bits["000000000000001000001000000"], t6_4);
    info.eval->multiply_plain(ss[4], bits["000000000000000000000000001"], t6_5);
    info.eval->add_many({t6_1, t6_2, t6_3, t6_4, t6_5}, ts[6]);
    
    info.eval->add(vs[2], ts[6], vs[5]); // __v5 = __v2 + __t6
    return vs[5];
}
    